class Ingest:
 """
 An Ingest context, that is, which dumps are being attempted for what execution date.
 """

 LMS\_DATE\_KEY = "lms\_date"

 @staticmethod
 def loading\_schema\_date\_key(ls: udp.LoadingSchema) -> str:
 return f"{ls.name}\_date"

 def \_\_init\_\_(self, execution\_date: datetime):
 self.execution\_date = execution\_date

 def set\_variable(self, key: str, set\_fn: Callable[[], Any]) -> None:
 """
 Sets Airdflow Variable Key to value returned by set\_fn if key is None
 and no override is defined
 """
 # override patch: search for other instances of "override path" for more.
 if override\_value := Variable.get(
 override\_key := KEY\_FMT.format(group="\*", key=key), None
 ):
 logger.warning(
 f"Override key '{override\_key}' already set as '{override\_value}'. Doing nothing."
 )
 return

 \_key = ingest\_variable\_key(self.execution\_date, key)
 if Variable.get(\_key, None) is None:
 val = set\_fn()
 if val is None:
 raise Exception(
 f"Could not set value for '{\_key}', no value was returned."
 )
 logger.info(f"Setting a new '{\_key}' value of {val}.")
 Variable.set(\_key, val)

 def get\_variable(self, key: str) -> Any:
 """
 Gets a value for an Airflow Variable Key, with extended functionality
 of supporting fetching overrides
 """
 # override patch: search for other instances of "override path" for more.
 if val := Variable.get(\_key := KEY\_FMT.format(group="\*", key=key), None):
 logger.warning(f"Using '{val}' from override key '{\_key}'.")
 return val

 \_key = ingest\_variable\_key(self.execution\_date, key)
 val = Variable.get(\_key)
 logger.info(f"Using '{val}' from '{\_key}'.")
 return val

 @property
 def lms\_fetcher(self):
 cd\_dated\_id = lms.CanvasDumpDatedId.from\_str(
 self.get\_variable(self.LMS\_DATE\_KEY)
 )
 return lms.LmsFetcher(dated\_id=cd\_dated\_id)

 def loading\_schema\_fetcher(self, ls: udp.LoadingSchema):
 ls\_folder = self.get\_variable(self.loading\_schema\_date\_key(ls))
 return loading\_schema.LoadingSchemaFetcher(
 loading\_schema=ls, folder\_name=ls\_folder
 )

