bool cropIsSimilar(const grid\_section & a, const grid\_section & b, float relativPosChangeLimit)
{
 float relativeX1Change = std::abs(a.x - b.x) / (float) AAA\_GRID\_WIDTH;
 float relativeY1Change = std::abs(a.y - b.y) / (float) AAA\_GRID\_HEIGHT;
 float relativeX2Change = std::abs(a.x + a.width - (b.x + b.width)) / (float) AAA\_GRID\_WIDTH;
 float relativeY2Change = std::abs(a.y + a.height - (b.y + b.height)) / (float) AAA\_GRID\_HEIGHT;

 focus\_logger\_debug\_print(2, "%s : relativ crop position change x1:%f y1:%f and x2:%f y2:%f
",
 \_\_func\_\_, relativeX1Change, relativeY1Change, relativeX2Change, relativeY2Change);

 return relativeX1Change <= relativPosChangeLimit &&
 relativeY1Change <= relativPosChangeLimit &&
 relativeX2Change <= relativPosChangeLimit &&
 relativeY2Change <= relativPosChangeLimit;
}

