grid\_section placeRoiInCrop(grid\_section crop, grid\_section roiOnSensor)
{
 // Slight simplification, ignoring AAA\_GRID\_PIX\_OFST
 float normalized\_x = static\_cast(roiOnSensor.x) / AAA\_GRID\_WIDTH;
 float normalized\_y = static\_cast(roiOnSensor.y) / AAA\_GRID\_HEIGHT;
 float normalized\_width = static\_cast(roiOnSensor.width) / AAA\_GRID\_WIDTH;
 float normalized\_height = static\_cast(roiOnSensor.height) / AAA\_GRID\_HEIGHT;

 // Calculating positions relative to crop, so (0, 0) is top left inside crop
 int start\_x = std::round(crop.width \* normalized\_x);
 int start\_y = std::round(crop.height \* normalized\_y);
 // 'end' is one past
 int end\_x = std::round(crop.width \* (normalized\_x + normalized\_width));
 int end\_y = std::round(crop.height \* (normalized\_y + normalized\_height));

 // Calculating relative to sensor, and clamping inside sensor
 start\_x = std::clamp(start\_x + crop.x, 0, AAA\_GRID\_WIDTH - 1);
 start\_y = std::clamp(start\_y + crop.y, 0, AAA\_GRID\_HEIGHT - 1);
 end\_x = std::clamp(end\_x + crop.x, 1, AAA\_GRID\_WIDTH);
 end\_y = std::clamp(end\_y + crop.y, 1, AAA\_GRID\_HEIGHT);

 return { .x = start\_x, .y = start\_y, .width = end\_x - start\_x, .height = end\_y - start\_y};
}

