float averageDistance(const std::vector & crops)
{
 float sumDist = 0.0;
 int numDist = 0;

 // focus\_logger\_debug\_print(1, "%s, crops: %zu
", \_\_func\_\_, crops.size());

 for (const auto & c : crops) {
 sumDist = std::accumulate(c.distances.begin(), c.distances.end(), sumDist);
 numDist += c.distances.size();
 }

 if (numDist == 0) {
 return 0.0;
 }
 else {
 return sumDist / numDist;
 }
}

