class Registry:
 """A registry providing name -> object mapping, to support
 custom modules.

 To create a registry (e.g. a backbone registry):

 .. code-block:: python

 BACKBONE\_REGISTRY = Registry('BACKBONE')

 To register an object:

 .. code-block:: python

 @BACKBONE\_REGISTRY.register()
 class MyBackbone(nn.Module):
 ...

 Or:

 .. code-block:: python

 BACKBONE\_REGISTRY.register(MyBackbone)
 """

 def \_\_init\_\_(self, name):
 self.\_name = name
 self.\_obj\_map = dict()

 def \_do\_register(self, name, obj, force=False):
 if name in self.\_obj\_map and not force:
 raise KeyError(
 'An object named "{}" was already '
 'registered in "{}" registry'.format(name, self.\_name)
 )

 self.\_obj\_map[name] = obj

 def register(self, obj=None, force=False):
 if obj is None:
 # Used as a decorator
 def wrapper(fn\_or\_class):
 name = fn\_or\_class.\_\_name\_\_
 self.\_do\_register(name, fn\_or\_class, force=force)
 return fn\_or\_class

 return wrapper

 # Used as a function call
 name = obj.\_\_name\_\_
 self.\_do\_register(name, obj, force=force)

 def get(self, name):
 if name not in self.\_obj\_map:
 raise KeyError(
 'Object name "{}" does not exist '
 'in "{}" registry'.format(name, self.\_name)
 )

 return self.\_obj\_map[name]

 def registered\_names(self):
 return list(self.\_obj\_map.keys())
